import { useLocation } from 'react-router-dom';
import { NDEFReader } from '@web-nfc/web-nfc';

function NfcCon() {
    const location = useLocation();

    const phoneNumber = location.state.phoneNumber;
    const countryCode = location.state.countryCode;

    async function readNFC() {
        try {
            const reader = new NDEFReader();

            await reader.scan();
            reader.onreading = event => {
                const decoder = new TextDecoder();
                for (const record of event.message.records) {
                    console.log("Record type: ", record.recordType);
                    console.log("Data: ", decoder.decode(record.data));
                }
            };
        } catch (error) {
            console.error("Error reading NFC: ", error);
        }
    }

    return (
        <div className="min-h-screen bg-gray-200 flex flex-col items-center justify-center p-4">
            <h1 className="mb-4 text-3xl font-bold">NFC Reader</h1>
            <p className="mb-8 text-lg">Scan an NFC tag with your device.</p>
            <button
                className="mb-4 p-4 rounded bg-blue-500 hover:bg-blue-600 text-white"
                onClick={readNFC}
            >
                Start NFC Scan
            </button>
            <div className="text-center">
                <p className="mb-2 font-medium">Details from Previous Page:</p>
                <p>Phone Number: {phoneNumber}</p>
                <p>Country Code: {countryCode}</p>
            </div>
        </div>
    );
}

export default NfcCon;
