import React, { useState, useEffect, useRef } from 'react';

function Header() {
    const [isOpen, setIsOpen] = useState(false);
    const openBoxRef = useRef(null);

    useEffect(() => {
        function handleClickOutside(event) {
            if (openBoxRef.current && !openBoxRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        }

        window.addEventListener('click', handleClickOutside);

        return () => {
            window.removeEventListener('click', handleClickOutside);
        };
    }, [openBoxRef]);

    return (
        <header className="p-4 bg-gray-100 text-black border-b border-gray-300">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className=" flex-grow text-2xl font-semibold items-center text-center">Jakkebiks</h1>
                <div>
                    <button onClick={() => {
                        setIsOpen(!isOpen);
                        console.log('isOpen:', isOpen);
                    }}>
                        <svg className="w-6 h-6" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7"></path>
                        </svg>
                    </button>
                    {isOpen && (
                        <div ref={openBoxRef} className="absolute right-0 mt-2 w-48 bg-white border border-gray-300 rounded shadow-xl">
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 1</a>
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 2</a>
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 3</a>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
}

export default Header;
