import React, { useState } from 'react';

function App() {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [selectedCountryCode, setSelectedCountryCode] = useState(countries[0].code);

    const handleKeyPress = (key) => {
        setPhoneNumber(prev => prev + key);
    }

    const handleClear = () => {
        setPhoneNumber('');
    }

    const handleDelete = () => {
        setPhoneNumber(prev => prev.slice(0, -1));
    }


    const [countries, setCountries] = useState([
        { name: 'Denmark', code: '+45' },
        { name: 'USA', code: '+1' },
        // ... add other countries here
    ]);

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
            <h2 className="mb-8 font-semibold text-2xl">Your phone number is your claim ticket</h2>

            <div className="mb-8 bg-white p-4 rounded w-64 flex items-center">
                <select
                    className="border p-2 mr-2"
                    value={selectedCountryCode}
                    onChange={(e) => {
                        setSelectedCountryCode(e.target.value);
                        // Now, you can prepend this to the phoneNumber or handle as required
                    }}
                >
                    {countries.map(country => (
                        <option key={country.code} value={country.code}>
                            {country.name}
                        </option>
                    ))}
                </select>

                <input
                    type="tel"
                    readOnly
                    className="w-full p-2 border-b mb-4 text-center"
                    value={selectedCountryCode + phoneNumber}
                />
                <div className="grid grid-cols-3 gap-4">
                    {['1', '2', '3', '4', '5', '6', '7', '8', '9', 'Clear', '0', 'X'].map(key => (
                        <button
                            key={key}
                            className="w-full h-10 bg-gray-200 rounded hover:bg-gray-300"
                            onClick={() => {
                                if (key === 'Clear') handleClear();
                                else if (key === 'X') handleDelete();
                                else handleKeyPress(key);
                            }}
                        >
                            {key}
                        </button>
                    ))}
                </div>
            </div>

            <button className="mb-4 p-2 rounded bg-blue-500 hover:bg-blue-600 text-white">
                Confirm
            </button>

            <p className="text-sm text-gray-600">
                By confirming, you agree to Privacy Policy &amp; Terms &amp; Conditions.
            </p>
        </div>
    );

}

export default App;
