import { createContext, useState, useEffect } from "react";
import jwt_decode from "jwt-decode";
import { useNavigate } from "react-router-dom";

const AuthContext = createContext();

export default AuthContext;


export const AuthProvider = ({ children }) => {

    let [authTokens, setAuthTokens] = useState(() => localStorage.getItem("authTokens") ? JSON.parse(localStorage.getItem("authTokens")) : null);
    let [user, setUser] = useState(() => localStorage.getItem("authTokens") ? jwt_decode(localStorage.getItem("authTokens")) : null);
    let [loading, setLoading] = useState(false);

    const navigate = useNavigate();


    let loginUser = async (e) => {
        e.preventDefault();
        console.log("Form submitted");
        let response = await fetch('http://127.0.0.1:8000/accounts/token/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 'username': e.target.username.value, 'password': e.target.password.value })
        })
        let data = await response.json();

        if (response.status === 200) {
            setAuthTokens(data);
            setUser(jwt_decode(data.access))
            localStorage.setItem("authTokens", JSON.stringify(data));
            navigate('/')
        } else {
            alert("Something went wrong")
        }
    }

    let logoutUser = () => {
        setAuthTokens(null);
        setUser(null);
        localStorage.removeItem("authTokens");
        navigate('/login')

    }


    let updateToken = async () => {
        console.log("Updating token called")

        // Check if refresh token exists
        if (!authTokens?.refresh) {
            console.log("Refresh token is not present. Logging out...")
            logoutUser();
            return;
        }

        let response = await fetch('http://127.0.0.1:8000/accounts/token/refresh/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 'refresh': authTokens.refresh })
        })
        let data = await response.json();

        if (response.status === 200) {
            setAuthTokens(data);
            setUser(jwt_decode(data.access))
            localStorage.setItem("authTokens", JSON.stringify(data));
        } else {
            console.log("Token refresh failed. Logging out...")
            logoutUser()
            if (loading) {
                setLoading(false)
            }
        }
    }

    let contextData = {
        user: user,
        authTokens: authTokens,
        loginUser: loginUser,
        logoutUser: logoutUser,
    }


    useEffect(() => {
        if (loading) {
            updateToken();
        }
        let fourMinutes = 1000 * 60 * 40;
        let interval = setInterval(() => {
            if (authTokens) {
                updateToken();
            }
        }, fourMinutes);
        return () => clearInterval(interval);
    }, [authTokens, loading]);




    return (
        <AuthContext.Provider value={contextData}>
            {loading ? null : children}

        </AuthContext.Provider>
    )
}
