import React, { useState, useRef, useEffect } from 'react';

function Header() {
    const [isOpen, setIsOpen] = useState(false);
    const menuRef = useRef(null);

    useEffect(() => {
        function handleOutsideClick(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        }

        document.addEventListener('mousedown', handleOutsideClick);

        return () => {
            document.removeEventListener('mousedown', handleOutsideClick);
        };
    }, []);

    return (
        <header className="p-4 bg-gray-100 text-black border-b border-gray-300">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className="flex-grow text-2xl font-semibold text-center">Jakkebiks</h1>
                <div>
                    <button onClick={(e) => {
                        e.stopPropagation();  // Prevent event from bubbling up
                        setIsOpen(!isOpen);
                    }}>
                        <svg
                            className="w-6 h-6"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                        >
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7"></path>
                        </svg>
                    </button>
                    {isOpen && (
                        <div ref={menuRef} className="absolute right-0 mt-2 w-48 bg-white border border-gray-300 rounded shadow-xl">
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 1</a>
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 2</a>
                            <a href="#" className="block px-4 py-2 text-black hover:bg-indigo-500 hover:text-white">Link 3</a>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
}

export default Header;
